    class Movie {
        // POPRIETA'
        public $title;
        public $or_language;
        public $year;
        public $description;

        // COSTRUTTORE
        function __construct($title, $or_language, $year, $description) {
            $this->title = $title;
            $this->or_language = $or_language;
            $this->year = $year;
            $this->description = $description;
        }

        // METODI
        public function getExcerpt($charsNumber = 200) {
            return substr($this->description, 0, $charsNumber) . "...";
        }
    }

    

